head.script:
	    $(function () {
            $("#PricingForm_FromCity, #PricingForm_PromoCode").val("");
            $("#PricingForm_ToCity, #OutDate, #RetDate").val("").prop("disabled", true);

	        $("#OutDate").datepicker({
                dateFormat: "dd/mm/yy", 
                minDate: 0,
                beforeShow: function (input) {
                    f.setCalendarBeforeShow(input, false);
                }
            });

            $("#RetDate").datepicker({
                dateFormat: "dd/mm/yy",
                minDate: 0,
                beforeShow: function(input) {
                    f.setCalendarBeforeShow(input, true);                    
                }
            });

            $.datepicker.setDefaults($.datepicker.regional["pl"]);

            $("#OutDate").change(function () {
                $("#RetDate").val("");
            });

            if ($("#errorText").text() !== "") {
                $('#modalPopup').modal('show');
            }

	    });

        String.prototype.isValidDate = function () {
            var fm = (arguments.length>0)?arguments[0].toUpperCase():'YYYYMMDD',
            dt = new Date(y=this.substr(n=fm.indexOf('Y'),fm.lastIndexOf('Y')-n+1), 
            m=this.substr(n=fm.indexOf('M'),fm.lastIndexOf('M')-n+1)-1,
            d=this.substr(n=fm.indexOf('D'),fm.lastIndexOf('D')-n+1));
            return dt.getDate()==d && dt.getMonth()==m && dt.getFullYear().toString().substr(-y.length,y.length)==y;
        };

        function today() {
            var dt = new Date();
            return new Date(dt.getFullYear(),dt.getMonth(),dt.getDate());
        }

        function htmlDecode(str) {
            return $("
").html(str).text();
        }

        function showModal(message) {
            $("#errorText").text(htmlDecode(message));
            $('#modalPopup').modal('show');
        }

	    function btnSubmit_onClick() {

            var numPax = document.getElementById("PricingForm_Adults");
            var dtOut = $("#OutDate").val();
            var dtRet = $("#RetDate").val();

            if (numPax.value == 0 || numPax.value == "") {
                showModal("Please enter the number of passengers travelling.");
	            return false;
	        }

	        if (document.getElementById("PricingForm_FromCity").value == "") {
                showModal("Wybierz miejsce wyjazdu.");
	            return false;
            }

	        if (document.getElementById("PricingForm_ToCity").value == "") {
                showModal("Wybierz miejsce przyjazdu.");
	            return false;
	        }

	        if (!dtOut.isValidDate('DD/MM/YYYY')) {
                showModal("Wprowadź datę wyjazdu.");
	            return false;
            } else {
                var doy = dtOut.split("/")[2];
                var dom = dtOut.split("/")[1];
                var dod = dtOut.split("/")[0];
                var dateOut = new Date(doy, dom-1, dod);
                if (dateOut < today()) {
                    showModal("Please select a outbound date of travel greater than today.");
	                return false;
	            }
	        }

            if (dtRet.length > 0) {
                if (!dtRet.isValidDate('DD/MM/YYYY')) {
                    showModal("Wprowadź datę wyjazdu.");
	                return false;
	            } else {                
                    var dry = dtRet.split("/")[2];
                    var drm = dtRet.split("/")[1];
                    var drd = dtRet.split("/")[0];
                    var dateReturn = new Date(dry, drm-1, drd);
                }
            }
            if (dtOut.length > 0 && dtRet.length > 0) { 
                if (dtOut.isValidDate('DD/MM/YYYY') && dtRet.isValidDate('DD/MM/YYYY')) {
                    if (dateOut > dateReturn) {
                        showModal("Please select a return date greater than your date of travel.");
	                    return false;
                    }
	            }
            }
            return true;
	    }

